mapper.sql:
where 1=1


	    and id =  #{id}


	    and org_id =  #{orgId}


	    and role =  #{role}


	    and follow_uid =  #{followUid}


	    and contract_id =  #{contractId}


	    and remark =  #{remark}


	    and create_time =  #{createTime}


	    and read_flag =  #{readFlag}


	    and check_state =  #{checkState}


    and loan_id =  #{loanId}

mapper.update:
update T_SALES_CHECK_LOG set
org_id = #{orgId, jdbcType=INTEGER}, role = #{role, jdbcType=VARCHAR}, follow_uid = #{followUid, jdbcType=VARCHAR}, contract_id = #{contractId, jdbcType=VARCHAR}, remark = #{remark, jdbcType=VARCHAR}, create_time = #{createTime, jdbcType=TIMESTAMP}, read_flag = #{readFlag, jdbcType=VARCHAR}, check_state = #{checkState, jdbcType=VARCHAR}, loan_id = #{loanId, jdbcType=VARCHAR},
where id = #{id}
