mapper.sql:
where 1=1


	    and id =  #{id}


	    and app_id =  #{appId}


	    and type =  #{type}


	    and name =  #{name}


	    and `key` =  #{key}


	    and value =  #{value}


	    and remarks =  #{remarks}


	    and state =  #{state}


	    and operator =  #{operator}


	    and org_id =  #{orgId}


	    and create_time =  #{createTime}


	    and update_time =  #{updateTime}

mapper.update:
update T_CAR_EXT set
app_id = #{appId, jdbcType=VARCHAR}, type = #{type, jdbcType=VARCHAR}, name = #{name, jdbcType=VARCHAR}, `key` = #{key, jdbcType=VARCHAR}, value = #{value, jdbcType=VARCHAR}, remarks = #{remarks, jdbcType=VARCHAR}, state = #{state, jdbcType=VARCHAR}, operator = #{operator, jdbcType=VARCHAR}, org_id = #{orgId, jdbcType=VARCHAR}, create_time = #{createTime, jdbcType=TIMESTAMP}, update_time = #{updateTime, jdbcType=TIMESTAMP},
where id = #{id}
