mapper.sql:
where 1=1


	    and id =  #{id}


	    and app_id =  #{appId}


	    and address =  #{address}


	    and area =  #{area}


	    and is_mortgage =  #{isMortgage}


	    and acquisition_date =  #{acquisitionDate}


	    and state =  #{state}


	    and operator =  #{operator}


	    and org_id =  #{orgId}


	    and create_time =  #{createTime}


	    and update_time =  #{updateTime}

mapper.update:
update T_CAR_HOUSING set
app_id = #{appId, jdbcType=VARCHAR}, address = #{address, jdbcType=VARCHAR}, area = #{area, jdbcType=INTEGER}, is_mortgage = #{isMortgage, jdbcType=INTEGER}, acquisition_date = #{acquisitionDate, jdbcType=DATE}, state = #{state, jdbcType=VARCHAR}, operator = #{operator, jdbcType=VARCHAR}, org_id = #{orgId, jdbcType=VARCHAR}, create_time = #{createTime, jdbcType=TIMESTAMP}, update_time = #{updateTime, jdbcType=TIMESTAMP},
where id = #{id}
