mapper.sql:
where 1=1


	    and id =  #{id}


	    and interviewing_id =  #{interviewingId}


	    and app_id =  #{appId}


	    and call_date =  #{callDate}


	    and on_relation =  #{onRelation}


	    and on_state =  #{onState}


	    and remarks =  #{remarks}


	    and operator =  #{operator}


	    and org_id =  #{orgId}


	    and create_time =  #{createTime}


	    and update_time =  #{updateTime}


	    and state =  #{state}

mapper.update:
update T_CREDIT_CALL_LOG set
interviewing_id = #{interviewingId, jdbcType=INTEGER}, app_id = #{appId, jdbcType=VARCHAR}, call_date = #{callDate, jdbcType=TIMESTAMP}, on_relation = #{onRelation, jdbcType=VARCHAR}, on_state = #{onState, jdbcType=VARCHAR}, remarks = #{remarks, jdbcType=VARCHAR}, operator = #{operator, jdbcType=VARCHAR}, org_id = #{orgId, jdbcType=VARCHAR}, create_time = #{createTime, jdbcType=TIMESTAMP}, update_time = #{updateTime, jdbcType=TIMESTAMP}, state = #{state, jdbcType=VARCHAR},
where id = #{id}
