	    and email =  #{email}

mapper.update:
update T_CREDIT_CONTACT set
app_id = #{appId, jdbcType=VARCHAR}, type = #{type, jdbcType=VARCHAR}, name = #{name, jdbcType=VARCHAR}, relation = #{relation, jdbcType=VARCHAR}, age = #{age, jdbcType=INTEGER}, phone = #{phone, jdbcType=VARCHAR}, mobile = #{mobile, jdbcType=VARCHAR}, id_type = #{idType, jdbcType=VARCHAR}, id_no = #{idNo, jdbcType=VARCHAR}, com_name = #{comName, jdbcType=VARCHAR}, add_province = #{addProvince, jdbcType=VARCHAR}, add_city = #{addCity, jdbcType=VARCHAR}, add_county = #{addCounty, jdbcType=VARCHAR}, address = #{address, jdbcType=VARCHAR}, state = #{state, jdbcType=VARCHAR}, operator = #{operator, jdbcType=VARCHAR}, org_id = #{orgId, jdbcType=VARCHAR}, create_time = #{createTime, jdbcType=TIMESTAMP}, update_time = #{updateTime, jdbcType=TIMESTAMP}, com_province = #{comProvince, jdbcType=VARCHAR}, com_city = #{comCity, jdbcType=VARCHAR}, com_county = #{comCounty, jdbcType=VARCHAR}, com_address = #{comAddress, jdbcType=VARCHAR}, com_duty = #{comDuty, jdbcType=VARCHAR}, com_add_date = #{comAddDate, jdbcType=DATE}, income = #{income, jdbcType=DOUBLE}, cooperation_year = #{cooperationYear, jdbcType=INTEGER}, cooperation_type = #{cooperationType, jdbcType=VARCHAR}, email = #{email, jdbcType=VARCHAR},
where id = #{id}

mapper.select:
SELECT * FROM
(
SELECT a.id,a.app_id,a.type,a.name,a.relation,a.age,a.phone,a.mobile,a.id_type,a.id_no,a.com_name,a.add_province,a.add_city,a.add_county,a.address,a.state,a.operator,a.org_id,a.create_time,a.update_time,a.com_province,a.com_city,a.com_county,a.com_address,a.com_duty,
a.com_add_date,a.income,a.cooperation_year,a.cooperation_type,a.email,b.contract_no
FROM T_CREDIT_CONTACT a,t_contract b 
WHERE 1=1 AND a.app_id=b.loan_app_id) c1 LEFT JOIN  
(
SELECT * FROM t_collection_record WHERE id IN(
SELECT MAX(id) FROM  t_collection_record c  GROUP BY answer_name,CONTRACT_NO  ) ) c2
ON c1.name=c2.answer_name AND c2.CONTRACT_NO=c1.CONTRACT_NO
where 1=1

	    and c1.id =  #{id}


	    and c1.app_id =  #{appId}


	    and c1.type =  #{type}


	    and c1.name =  #{name}


	    and c1.relation =  #{relation}


	    and c1.age =  #{age}


	    and c1.phone =  #{phone}


	    and c1.mobile =  #{mobile}


	    and c1.id_type =  #{idType}


	    and c1.id_no =  #{idNo}


	    and c1.com_name =  #{comName}


	    and c1.add_province =  #{addProvince}


	    and c1.add_city =  #{addCity}


	    and c1.add_county =  #{addCounty}


	    and c1.address =  #{address}


	    and c1.state =  #{state}


	    and c1.operator =  #{operator}


	    and c1.org_id =  #{orgId}


	    and c1.create_time =  #{createTime}


	    and c1.update_time =  #{updateTime}


	    and c1.com_province =  #{comProvince}


	    and c1.com_city =  #{comCity}


	    and c1.com_county =  #{comCounty}


	    and c1.com_address =  #{comAddress}


	    and c1.com_duty =  #{comDuty}


	    and c1.com_add_date =  #{comAddDate}


	    and c1.income =  #{income}


	    and c1.cooperation_year =  #{cooperationYear}


	    and c1.cooperation_type =  #{cooperationType}


	    and c1.email =  #{email}


	 	and c1.contract_no =  #{contractNo}
