mapper.sql:
where 1=1


	    and id =  #{id}


	    and app_id =  #{appId}


	    and repeat_app_id =  #{repeatAppId}


	    and type =  #{type}


	    and value =  #{value}


	    and comment =  #{comment}


	    and state =  #{state}


	    and operator =  #{operator}


	    and org_id =  #{orgId}


	    and create_time =  #{createTime}


	    and update_time =  #{updateTime}

mapper.update:
update T_CREDIT_REPEAT_DETAIL set
app_id = #{appId, jdbcType=VARCHAR}, repeat_app_id = #{repeatAppId, jdbcType=VARCHAR}, type = #{type, jdbcType=VARCHAR}, value = #{value, jdbcType=VARCHAR}, comment = #{comment, jdbcType=VARCHAR}, state = #{state, jdbcType=VARCHAR}, operator = #{operator, jdbcType=VARCHAR}, org_id = #{orgId, jdbcType=VARCHAR}, create_time = #{createTime, jdbcType=TIMESTAMP}, update_time = #{updateTime, jdbcType=TIMESTAMP},
where id = #{id}

mapper.select:
SELECT *FROM ( SELECT i.`APP_ID` repeat_app_id, CONCAT( '居住地址', '单位地址', '单位名称', '与申请(APP_ID:',i.`APP_ID`,')重复') COMMENT ,'${address}' VALUE ,'${comAddress}' VALUE ,'${comName}' VALUE
,#{appId} APP_ID
,#{type} TYPE
FROM T_CREDIT_INTERVIEW i WHERE i.STATE='1'
AND i.APP_ID !=#{appId}
AND i.`ADDRESS`=#{address} AND i.`ADDRESS_FLAG`='0'
AND i.`COM_ADDRESS`=#{comAddress} AND i.`COM_ADDRESS_FLAG`='0'
AND i.`COM_NAME`=#{comName} AND i.`COM_NAME_FLAG`='0'
UNION SELECT a.`APP_ID` repeat_app_id, CONCAT( '居住地址', '单位地址', '单位名称', '与申请(APP_ID:',a.`APP_ID`,')重复') COMMENT ,'${address}' VALUE ,'${comAddress}' VALUE ,'${comName}' VALUE
,#{appId} APP_ID
,#{type} TYPE
FROM T_CREDIT_APP a WHERE 1=1
AND a.`APP_ID` !=#{appId}
AND a.`ADDRESS`=#{address}
AND a.`COM_ADDRESS`=#{comAddress}
AND a.`COM_NAME`=#{comName}
) crd
