mapper.sql:
where 1=1


	    and id =  #{id}


	    and app_id =  #{appId}


	    and type =  #{type}


	    and credit_card_exception =  #{creditCardException}


	    and query_six_count =  #{querySixCount}


	    and max_default =  #{maxDefault}


	    and amount =  #{amount}


	    and default_amount =  #{defaultAmount}


	    and default_count =  #{defaultCount}


	    and default_max_count =  #{defaultMaxCount}


	    and default_ninety_count =  #{defaultNinetyCount}


	    and expire_loan =  #{expireLoan}


	    and expire_housing =  #{expireHousing}


	    and state =  #{state}


	    and operator =  #{operator}


	    and org_id =  #{orgId}


	    and create_time =  #{createTime}


	    and update_time =  #{updateTime}


	    and used_amount =  #{usedAmount}

mapper.update:
update T_CREDIT_REPORT set
app_id = #{appId, jdbcType=VARCHAR}, type = #{type, jdbcType=VARCHAR}, credit_card_exception = #{creditCardException, jdbcType=INTEGER}, query_six_count = #{querySixCount, jdbcType=INTEGER}, max_default = #{maxDefault, jdbcType=INTEGER}, amount = #{amount, jdbcType=DOUBLE}, default_amount = #{defaultAmount, jdbcType=DOUBLE}, default_count = #{defaultCount, jdbcType=INTEGER}, default_max_count = #{defaultMaxCount, jdbcType=INTEGER}, default_ninety_count = #{defaultNinetyCount, jdbcType=INTEGER}, expire_loan = #{expireLoan, jdbcType=INTEGER}, expire_housing = #{expireHousing, jdbcType=INTEGER}, state = #{state, jdbcType=VARCHAR}, operator = #{operator, jdbcType=VARCHAR}, org_id = #{orgId, jdbcType=VARCHAR}, create_time = #{createTime, jdbcType=TIMESTAMP}, update_time = #{updateTime, jdbcType=TIMESTAMP}, used_amount = #{usedAmount, jdbcType=DOUBLE},
where id = #{id}
