mapper.sql:
where 1=1


	    and id =  #{id}


	    and app_id =  #{appId}


	    and submit_info =  #{submitInfo}


	    and result =  #{result}


	    and result_info =  #{resultInfo}


	    and submit_operator =  #{submitOperator}


	    and operator =  #{operator}


	    and state =  #{state}


	    and org_id =  #{orgId}


	    and create_time =  #{createTime}


	    and update_time =  #{updateTime}

mapper.update:
update T_HOUSE_ANTIFRAUD set
app_id = #{appId, jdbcType=VARCHAR}, submit_info = #{submitInfo, jdbcType=VARCHAR}, result = #{result, jdbcType=VARCHAR}, result_info = #{resultInfo, jdbcType=VARCHAR}, submit_operator = #{submitOperator, jdbcType=VARCHAR}, operator = #{operator, jdbcType=VARCHAR}, state = #{state, jdbcType=VARCHAR}, org_id = #{orgId, jdbcType=VARCHAR}, create_time = #{createTime, jdbcType=TIMESTAMP}, update_time = #{updateTime, jdbcType=TIMESTAMP},
where id = #{id}
