mapper.sql:
where 1=1


	    and id =  #{id}


	    and key_name =  #{keyName}


	    and key_prop =  #{keyProp}


	    and key_value =  #{keyValue}


	    and description =  #{description}


	    and parent_key_prop =  #{parentKeyProp}

mapper.update:
update T_DATA_DICTIONARY set
id = #{id, jdbcType=INTEGER}, key_name = #{keyName, jdbcType=VARCHAR}, key_prop = #{keyProp, jdbcType=VARCHAR}, key_value = #{keyValue, jdbcType=VARCHAR}, description = #{description, jdbcType=VARCHAR}, parent_key_prop = #{parentKeyProp, jdbcType=VARCHAR},
where id = #{id}
