package com.hikootech.mqcash.po;import java.io.Serializable;import java.util.Date;public class UserCreditRecord implements Serializable {		private static final long serialVersionUID = -7215345544134942751L;	/** id **/	private String creditId;	/** 征信记录表id **/	private String infoId;	/** 身份证号 **/	private String idCard;	/** 用户真实姓名 **/	private String name = "";	/** 身份证上写的住址 **/	private String idCardAddress = "";	/** 申请联系电话 **/	private String contactPhone = "";	/** 用户访问网掌厅ip **/	private String custIp = "";	/** 订单来源（渠道） **/	private Integer source;	/** 是否征信通过：0通过 1不通过 **/	private Integer isPass;	/** 原因描述 **/	private String reason;	/**  **/	private Date createTime;	/**  **/	private Date updateTime;	/**  **/	private String operator;	public String getCreditId() {		return creditId;	}	public void setCreditId(String creditId) {		this.creditId = creditId;	}	public String getIdCard() {		return idCard;	}	public void setIdCard(String idCard) {		this.idCard = idCard;	}	public Integer getIsPass() {		return isPass;	}	public void setIsPass(Integer isPass) {		this.isPass = isPass;	}	public String getReason() {		return reason;	}	public void setReason(String reason) {		this.reason = reason;	}	public Date getCreateTime() {		return createTime;	}	public void setCreateTime(Date createTime) {		this.createTime = createTime;	}	public Date getUpdateTime() {		return updateTime;	}	public void setUpdateTime(Date updateTime) {		this.updateTime = updateTime;	}	public String getOperator() {		return operator;	}	public void setOperator(String operator) {		this.operator = operator;	}	public Integer getSource() {		return source;	}	public void setSource(Integer source) {		this.source = source;	}	public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}	public String getIdCardAddress() {		return idCardAddress;	}	public void setIdCardAddress(String idCardAddress) {		this.idCardAddress = idCardAddress;	}	public String getContactPhone() {		return contactPhone;	}	public void setContactPhone(String contactPhone) {		this.contactPhone = contactPhone;	}	public String getCustIp() {		return custIp;	}	public void setCustIp(String custIp) {		this.custIp = custIp;	}	public String getInfoId() {		return infoId;	}	public void setInfoId(String infoId) {		this.infoId = infoId;	}	public String toString() {		StringBuffer sb=new StringBuffer();		sb.append("{");		sb.append("'creditId':'"+this.getCreditId()+"',");		sb.append("'idCard':'"+this.getIdCard()+"',");		sb.append("'contactPhone':'"+this.getContactPhone()+"',");		sb.append("'isPass':'"+this.getIsPass()+"',");		sb.append("'reason':'"+this.getReason()+"',");		sb.append("'createTime':'"+this.getCreateTime()+"',");		sb.append("'updateTime':'"+this.getUpdateTime()+"',");		sb.append("'operator':'"+this.getOperator()+"'");		sb.append("}");		return sb.toString();	}}