package com.hikootech.mqcash.util;

import java.io.BufferedOutputStream;
import java.io.File;
import java.io.FileOutputStream;
import java.io.IOException;
/**
 * 
 * @Author liheng
 * @Create In 2015年10月31日
 */
public class FileUtil {
	
	
	
	 /**
		 * 把字节数组保存为一个文件
		 * 
		 * @param b
		 * @param outputFile
		 * @return
		 */
		public static File getFileFromBytes(byte[] b, String outputFile) {
			File ret = null;
			BufferedOutputStream stream = null;
			try {
				ret = new File(outputFile);
				FileOutputStream fstream = new FileOutputStream(ret);
				stream = new BufferedOutputStream(fstream);
				stream.write(b);
				stream.flush();
			} catch (Exception e) {
				e.printStackTrace();
			} finally {
				if (stream != null) {
					try {
						stream.close();
					} catch (IOException e) {
						e.printStackTrace();
					}
				}
			}
			return ret;
		}
	

	public static void main(String[] args) {
		//responseData---{"id_number":"131124198905090227","merchant_no":"100000","name_cn":"段亚荣","photo":
		//""
		//,"ret_code":"000000","ret_code_desc":"成功","version":"1.0.0"}
		String str="";
		byte[] b=BASE64Util.decode(str);
		getFileFromBytes(b, "d:/123.jpg");
		System.out.println("process ok!");

	}

}
